/** Carousel: displays images and arrows to navigate through them
 * 
 * Props:
 * - photos: array of {src, caption} objects
 * - title: string describing the collection of images
 * 
 * State:
 * - currCardIdx: integer for current card index
 * 
 * App --> Carousel --> Card
 */
function Carousel({ photos }) {
  const [currCardIdx, setCurrCardIdx] = useState(0);

  const nextCard = () => {
    setCurrCardIdx((currCardIdx + 1) % photos.length);
  };

  const prevCard = () => {
    setCurrCardIdx((currCardIdx - 1 + photos.length) % photos.length);
  };

  // Check if the current photo is defined
  const currentPhoto = photos && photos[currCardIdx];

  return (
    <div>
      {currentPhoto && (
        <Card 
          caption={currentPhoto.caption} 
          src={currentPhoto.src} 
          currNum={currCardIdx + 1} 
          totalNum={photos.length} 
        />
      )}
      <button onClick={prevCard} disabled={currCardIdx === 0}>Previous</button>
      <button onClick={nextCard} disabled={currCardIdx === photos.length - 1}>Next</button>
    </div>
  );
}


export default Carousel;
