import { useState } from "react";
import "./Carousel.css";
import Card from "./Card";

function Carousel({ cards }) {
  // Initialize state variable for the current card index
  const [currCardIdx, setCurrCardIdx] = useState(0);

  // Function to go to the next card
  const nextCard = () => {
    setCurrCardIdx((currCardIdx + 1) % cards.length);
  };

  // Function to go to the previous card
  const prevCard = () => {
    setCurrCardIdx((currCardIdx - 1 + cards.length) % cards.length);
  };

  return (
    <div>
      <Card {...cards[currCardIdx]} currNum={currCardIdx + 1} totalNum={cards.length} />
      <button onClick={prevCard}>Previous</button>
      <button onClick={nextCard}>Next</button>
    </div>
  );
}
